    private void tabeldataMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabeldataMouseClicked
        // TODO add your handling code here:

        int input  = tabeldata.getSelectedRow();
        DefaultTableModel tm = (DefaultTableModel) tabeldata.getModel();
        id.setText(tm.getValueAt(input,0).toString());
        name.setText(tm.getValueAt(input,1).toString());
        password.setText(tm.getValueAt(input,2).toString());
        birthdate.setDateFormatString(tm.getValueAt(input,3).toString());
        hp.setText(tm.getValueAt(input,4).toString());
        kelamin.setSelectedItem(tm.getValueAt(input,5).toString());
        checkin.setDateFormatString(tm.getValueAt(input,6).toString());
        checkout.setDateFormatString(tm.getValueAt(input,7).toString());
        type.setSelectedItem(tm.getValueAt(input,8).toString());
        nokamar.setSelectedItem(tm.getValueAt(input,9).toString());
        harga.setText(tm.getValueAt(input,10).toString());
    }//GEN-LAST:event_tabeldataMouseClicked

