    private void updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateActionPerformed
        //Membuat format tanggal di Java sama dengan Mysql
        String tampilan ="yyyy-MM-dd";
        SimpleDateFormat fm = new SimpleDateFormat(tampilan);
        try{
            //Menghubungkan antara client server dan database
            Interfacehotel dbi=(Interfacehotel)Naming.lookup("rmi://localhost:2021/costumer");
            //Menyimpan hasil data dari Client ke tiap-tiap entitas
            int upid =Integer.parseInt(id.getText());
            String upusername = name.getText();
            String upaddress = password.getText();
            //String upbirthdate = birthdate.setDateFormatString();
            String upbirthdate = String.valueOf(fm.format(birthdate.getDate()));
            String upnohp=hp.getText();
            String upkelamin=kelamin.getSelectedItem().toString();
            String upcekin = String.valueOf(fm.format(checkin.getDate()));
            String upcekout = String.valueOf(fm.format(checkout.getDate()));
            String uptykamar=type.getSelectedItem().toString();
            String upkamar=nokamar.getSelectedItem().toString();
            String upprice=harga.getText();

            String result = dbi.Update(upid, upusername, upaddress, upbirthdate, upnohp, upkelamin, upcekin, upcekout, uptykamar, upkamar, upprice);
            DefaultTableModel defTable = (DefaultTableModel) tabeldata.getModel();
            defTable.setRowCount(0);
            //Menampilkan data hasil inputan
            tampilancostumer();
            //Menampilkan feedback dari server
            JOptionPane.showMessageDialog(null, result,"Sukses",JOptionPane.INFORMATION_MESSAGE);
            //Menghapus inputan
            clearInput();
            //Menampilkan error saaat mengupdate data
        } catch (NumberFormatException | NotBoundException | MalformedURLException | RemoteException | HeadlessException ex) {
            JOptionPane.showMessageDialog(null, ex,"ERROR",JOptionPane.ERROR_MESSAGE);

        }
    }//GEN-LAST:event_updateActionPerformed

